con:configuration.scriptText:
import se.skl.rivta.tksupport.soapui.ContentAssertions
def ca = new ContentAssertions(context, messageExchange, log)
ca.assertSchematronConstraints()

import se.skl.rivta.tksupport.soapui.ContentAssertions
def ca = new ContentAssertions(context, messageExchange)

context.allRecords = ca.getContentRecords()

con:config.script:
def authorTime = '/urn:diagnosis/urn1:diagnosisHeader/urn1:accountableHealthcareProfessional/urn1:authorTime'
def signatureTime = '/urn:diagnosis/urn1:diagnosisHeader/urn1:legalAuthenticator/urn1:signatureTime'
def diagnosisTime = '/urn:diagnosis/urn1:diagnosisBody/urn1:diagnosisTime'

if (!context.allRecords) {
	testRunner.fail("No records")
	return
}
if (!context.dates) {
	def authorTimes = context.allRecords.collect { record -> record.firstElementAt(authorTime) }
	def signatureTimes = context.allRecords.collect { record -> record.firstElementAt(signatureTime) }
	def diagnosisTimes = context.allRecords.collect { record -> record.firstElementAt(diagnosisTime) }

	context.dates = authorTimes.findAll().collect{ it[0..7] }.unique()
	context.dates += signatureTimes.findAll().collect{ it[0..7] }.unique()
	context.dates += diagnosisTimes.findAll().collect{ it[0..7] }.unique()

	context.dates = context.dates.findAll().collect{ it[0..7] }.unique()
	log.info "Unika datum: " + context.dates

	if (!context.dates) {
		testRunner.fail("No data")
	}
}

context.timePeriodStart = context.dates.pop()
context.timePeriodEnd = context.timePeriodStart

[context.timePeriodStart, context.timePeriodEnd]

con:configuration.scriptText:
import se.skl.rivta.tksupport.soapui.ContentAssertions
def ca = new ContentAssertions(context, messageExchange, log)
ca.assertSchematronConstraints()

import se.skl.rivta.tksupport.soapui.ContentAssertions
def ca = new ContentAssertions(context, messageExchange)

def authorTime = '/urn:diagnosis/urn1:diagnosisHeader/urn1:accountableHealthcareProfessional/urn1:authorTime'
def signatureTime = '/urn:diagnosis/urn1:diagnosisHeader/urn1:legalAuthenticator/urn1:signatureTime'
def diagnosisTime = '/urn:diagnosis/urn1:diagnosisBody/urn1:diagnosisTime'

def id = '/urn:diagnosis/urn1:diagnosisHeader/urn1:documentId'

def condition = { record ->
	def authorTimeWithin = record.timestampInRange(authorTime, context.timePeriodStart + '000000', context.timePeriodEnd + '235959')
	def signatureTimeWithin = record.firstElementAt(signatureTime) != null && record.timestampInRange(signatureTime, context.timePeriodStart + '000000', context.timePeriodEnd + '235959')
	def diagnosisTimeWithin = record.firstElementAt(diagnosisTime) != null && record.timestampInRange(diagnosisTime, context.timePeriodStart + '000000', context.timePeriodEnd + '235959')

	return (authorTimeWithin || signatureTimeWithin || diagnosisTimeWithin)
}

def recordInfo = { record ->
	sprintf("DocumentId %s (authorTime:%s signatureTime:%s diagnosisTime:%s)", [id, authorTime, signatureTime, diagnosisTime ].collect{record.firstElementAt(it)})
}

ca.assertFilteredResponse(context.allRecords, condition, recordInfo)

con:config.script:
if (context.dates.any()) {
	testRunner.gotoStepByName( "Calculate interval")
}
else if (testRunner.getResults().any{it.getStatus().toString() == 'FAILED'}) {
	testRunner.fail("One or more steps failed")
}

