//Läs in respons och omvandla tecken
def xml = holder.getXml()
def unescapedXml = xml.replaceAll(/&amp;/, '&')
				.replaceAll(/&lt;/, '<')
         			.replaceAll(/&gt;/, '>')
         			.replaceAll(/&quot;/, '"')
         			.replaceAll(/&apos;/, "'")

//Läs in teststrängen och omvandla tecken
