def unescapedTestString = testString.replaceAll(/&amp;/, '&')
				.replaceAll(/&lt;/, '<')
         			.replaceAll(/&gt;/, '>')
         			.replaceAll(/&quot;/, '"')
         			.replaceAll(/&apos;/, "'")

assert unescapedXml.indexOf(unescapedTestString) > -1 : "Söksträngen " + unescapedTestString + " finns inte"

