con:config.script:
def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context )

// Hämta responser och nolla logId för att kunna jämföra svaren
def holder1 = groovyUtils.getXmlHolder( "Call GetDiagnosis 1#Response" )
def xml1 = new XmlSlurper().parseText(holder1.getXml())
xml1.Body.GetDiagnosisResponse.result.logId = 0

def holder2 = groovyUtils.getXmlHolder( "Call GetDiagnosis 2#Response" )
def xml2 = new XmlSlurper().parseText(holder2.getXml())
xml2.Body.GetDiagnosisResponse.result.logId = 0

assert xml1 == xml2 : "Konsumenterna får inte samma svar"
