def padl = '/urn:functionalStatusAssessment/urn1:functionalStatusAssessmentBody/urn1:padl'
def code = '/urn:functionalStatusAssessment/urn1:functionalStatusAssessmentBody/urn1:padl/urn1:typeOfAssessment/urn1:code'
