def originalText = '/urn:functionalStatusAssessment/urn1:functionalStatusAssessmentBody/urn1:padl/urn1:typeOfAssessment/urn1:originalText'

ca.assertForAnyRecord(
{
   record -> 
	def padlArray = record.allElementsAt(padl)
	def codeArray = record.allElementsAt(code)
	def displayNameArray = record.allElementsAt(displayName)
	def originalTextArray = record.allElementsAt(originalText)

	for (i = 0; i < padlArray.size(); i++) {
		if(displayNameArray[i].equals("personlig hygien")) {
			return true
		}
		if(originalTextArray[i].equals("personlig hygien")) {
			return true
		}
	}

	return false                      	
},

 "At least one record must contain a padl with originalText set to 'personlig hygien' OR code set to 'pad-pad-peh' and displayName set to 'personlig hygien'"
)

