con:configuration.scriptText:
import se.skl.rivta.tksupport.soapui.ContentAssertions
def ca = new ContentAssertions(context, messageExchange, log)
ca.assertSchematronConstraints()

import se.skl.rivta.tksupport.soapui.ContentAssertions
def ca = new ContentAssertions(context, messageExchange)

def padl = '/urn:functionalStatusAssessment/urn1:functionalStatusAssessmentBody/urn1:padl'
def code = '/urn:functionalStatusAssessment/urn1:functionalStatusAssessmentBody/urn1:padl/urn1:typeOfAssessment/urn1:code'
def displayName = '/urn:functionalStatusAssessment/urn1:functionalStatusAssessmentBody/urn1:padl/urn1:typeOfAssessment/urn1:displayName'
def originalText = '/urn:functionalStatusAssessment/urn1:functionalStatusAssessmentBody/urn1:padl/urn1:typeOfAssessment/urn1:originalText'

ca.assertForAnyRecord(
{
   record -> 
	def padlArray = record.allElementsAt(padl)
	def codeArray = record.allElementsAt(code)
	def displayNameArray = record.allElementsAt(displayName)
	def originalTextArray = record.allElementsAt(originalText)

	for (i = 0; i < padlArray.size(); i++) {
		if(displayNameArray[i].equals("på/avklädning")) {
			return true
		}
		if(originalTextArray[i].equals("på/avklädning")) {
			return true
